mapper.select:
SELECT id,
name,createDate,createUserId,createUserName,modifyDate,modifyUserId,modifyUserName,isDel,version
FROM express_company


		AND name = #{name}


		AND createDate = #{createDate}


		AND createUserId = #{createUserId}


		AND createUserName = #{createUserName}


		AND modifyDate = #{modifyDate}


		AND modifyUserId = #{modifyUserId}


		AND modifyUserName = #{modifyUserName}
